$(document).ready(function(){var e=$(".slide-wrapper").flickity({cellAlign:"left",contain:!0,pageDots:!1,prevNextButtons:!1}),t=e.data("flickity"),l=$(".button-group--cells"),i=l.find(".tab");e.on("cellSelect",function(){i.filter(".is-selected").removeClass("is-selected"),i.eq(t.selectedIndex).addClass("is-selected")}),l.on("click",".tab",function(){var t=$(this).index();e.flickity("select",t)})});